    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

    int DA, KM, RD;
    double RK=0, Tot=0, RK2, RK3, Tot1=0;
    
    DA=Integer.parseInt(diA.getText());
    KM=Integer.parseInt(km.getText());
    
    if(pe.isSelected()){////////////////////////////
        
        if(KM<=99){
        RD=DA*300;
         RK=2*KM;
           Tot=RD+RK;
        }
        else{
        if(KM>=100){
           RD=DA*300;
            RK2=2*KM;
            RK=KM*2.5;
            RK3=RK2+RK;
            Tot=RD+RK3;
            Tot1=RD+RK;

        }
        }
        area.setText("RESULTADO: " + "\n");
        area.append("DIAS ALQUILADOS: " + DA + "         KILOMETROS RECORRIDOS: " + KM + "\n" + "TARIFA DE AUTO: $300 " + "\nTARIFA DE KILOMETROS: $2"+"\n"  + "TOTAL A PAGAR SIN EL 2.5%:" + Tot1 + "\n" + "TOTAL A PAGAR= " + Tot);
        
    }
     if(me.isSelected()){///////////////////
        RD=DA*400;
          if(KM<=99){
              RD=DA*400;
              RK=3*KM;
              Tot=RD+RK;
        }
        else{
        if(KM>=100){
          RD=DA*400;
            RK2=3*KM;
            RK=KM*2.5;
            RK3=RK2+RK;
            Tot=RD+RK3;
            Tot1=RD+RK;

         
        }
        }
        area.setText("RESULTADO: " + "\n" + "");
        area.append("DIAS ALQUILADOS: " + DA + "         KILOMETROS RECORRIDOS: " + KM + "\n" + "TARIFA DE AUTO: $400 " + "\nTARIFA DE KILOMETROS: $3"+"\n"  + "TOTAL A PAGAR SIN EL 2.5%:" + Tot1 + "\n" + "TOTAL A PAGAR= " + Tot
);

    }
     if(ge.isSelected()){/////////////////////
          if(KM<=99){
              RD=DA*600;
              RK=4*KM;
              Tot=RD+RK;
        }
        else{
        if(KM>=100){
           RD=DA*600;
            RK2=4*KM;
            RK=KM*2.5;
            RK3=RK2+RK;
            Tot=RD+RK3;
            Tot1=RD+RK;

        }
        }
        area.setText("RESULTADO: " + "\n");
        area.append("DIAS ALQUILADOS: " + DA + "         KILOMETROS RECORRIDOS: " + KM + "\n" + "TARIFA DE AUTO: $600 " + "\nTARIFA DE KILOMETROS: $4"+"\n"  + "TOTAL A PAGAR SIN EL 2.5%:" + Tot1 + "\n" + "TOTAL A PAGAR= " + Tot
);

        
    }
// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

